import { Injectable } from '@angular/core';
import { Program } from '../program';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/environments/environment';

@Injectable({
  providedIn: 'root'
})
export class ProgramService {

  programs!: Program[];

  constructor(private http: HttpClient) { }

  list() {
    return this.http.get(`${environment.apiUrl}/auth/register`);
  }

  create(data: Program) {
    return this.http.post('http://127.0.0.1:8080/api/v1/auth/register', data);
  }

  update(id: number, data: Program) {
    return this.http.put(`http://127.0.0.1:8080/api/v1/auth/register/${id}`, data);
  }

  delete(id: number){
    return this.http.delete(`http://127.0.0.1:8080/api/v1/auth/register/${id}`);
  }

}
