import { Component, OnInit } from '@angular/core';

import { FormBuilder, FormGroup } from '@angular/forms';
import { Validators } from '@angular/forms';
import { FormArray } from '@angular/forms';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})

export class RegisterComponent implements OnInit{
  registerForm!: FormGroup ;

  constructor(private fb: FormBuilder) { }

  ngOnInit() {
      this.registerForm = this.fb.group({
        firstName: ['', Validators.required],
        lastName: ['', Validators.required],
        /* address: this.fb.group({
          street: [''],
          city: [''],
          state: [''],
          zip: ['']
        }),
        aliases: this.fb.array([
          this.fb.control('')
        ]) */
      });
  } 
  signUp(){
    console.log(this.registerForm.value);
  }
}

<form [formGroup]="registerForm" (ngSubmit)="signUp()">
<div class="form-group"></div>
  <label for="firstName">Prénom</label><br>
  <input id="firstName" type="text" formControlName="firstName" required><br>

<div class="form-group">
  <label for="lastName">Nom</label>
  <input id="lastName" type="text" formControlName="lastName" required><br></div>

<div class="form-group">
  <label for="email">E-mail</label><br>
  <input id="email" type="text" formControlName="email" required><br>
</div>

<div class="form-group">
  <label for="phoneNumber">Numéro de téléphone</label><br>
  <input id="phoneNumber" type="text" formControlName="phoneNumber"  required><br>
</div>

<div class="form-group">
  <label for="role">Rôle</label><br>
  <input id="role" type="text" formControlName="role" required><br>
</div>

<div class="form-group">
  <label for="password">Mot de passe</label><br>
  <input id="password" type="text" formControlName="password" required><br>
</div>

<div class="form-group">
  <button type="submit" [disabled]="!registerForm.valid">Submit</button>
</div>
</form>
