import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Program } from 'src/app/program';
import { ProgramService } from 'src/app/services/program.service';

@Component({
  selector: 'app-program-form',
  templateUrl: './program-form.component.html',
  styleUrls: ['./program-form.component.scss']
})

export class ProgramFormComponent implements OnInit {
  programForm!: FormGroup ;

  constructor(private fb: FormBuilder, private program: ProgramService) { }

  ngOnInit() {
      this.programForm = this.fb.group({
        name: ['', Validators.required],
        domainNames: ['', Validators.required],
        description: ['', Validators.required],
        startDate: ['', Validators.required],
        endDate: ['', Validators.required],
        coachId: ['', Validators.required],

      });
  }

  createProgram(){
    const unProgram = this.programForm.value as Program;
    unProgram.domainNames = this.programForm.value.domainNames.split(',');
    //unProgram.startDate = '27/04/2023';
    this.program.create(unProgram).subscribe(
      (res) => { console.log(res); },
      (error) => { console.error(error); }
    );
    //this.auth.register
    console.log(this.programForm.value);
  }

}

<link href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-EVSTQN3/azprG1Anm3QDgpJLIm9Nao0Yz1ztcQTwFspd3yD65VohhpuuCOmLASjC" crossorigin="anonymous">
<script src="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/js/bootstrap.bundle.min.js" integrity="sha384-MrcW6ZMFYlzcLA8Nl+NtUVF0sA7MsXsP1UyJoMp4YLEuNSfAP+JcXn/tWtIaxVXM" crossorigin="anonymous"></script>

<ng-container *ngIf="programForm">
  <div class="container">
    <form [formGroup]="programForm" (ngSubmit)="createProgram()" class="form">
    <div class="form-group mt-5">
      <label for="name" class="form-label" >Libelle</label>
      <input id="name" type="text" formControlName="name" class="form-control">
      <div class="form-error" color="red"
            *ngIf="(programForm.controls['name'].touched) && 
              programForm.controls['name'].errors &&
              programForm.controls['name'].errors['required']"
        >
        Libelle Programme
      </div>
    </div>

    <div class="form-group mt-3">
      <label for="domainNames">Domaines</label>
      <input id="domainNames" type="text" formControlName="domainNames"  class="form-control">
    </div>

    <div class="form-group mt-3">
      <label for="description">Description</label>
      <input id="description" type="text" formControlName="description"  class="form-control">
    </div>

    <div class="form-group mt-3">
      <label for="startDate">Date dédut</label>
      <input id="startDate" type="date" formControlName="startDate"   class="form-control">
    </div>

    <div class="form-group mt-3">
      <label for="endDate">Date fin</label>
      <input id="endDate" type="date" formControlName="endDate"   class="form-control">
    </div>

     <div class="form-group mt-3">
        <label for="coachId">Coach</label>
        <select id="coachId" class="form-control" formControlName="coachId">
            <option value="1">Diery</option>
            <option value="2">Alioune</option>
        </select>
    </div>


    <div class="form-group mt-3">
      <button type="submit" [disabled]="!programForm.valid" class="btn btn-primary mb-3">Enregistrer</button>
    </div>
    </form>
  </div>
</ng-container>
