<script src="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/js/bootstrap.bundle.min.js" integrity="sha384-MrcW6ZMFYlzcLA8Nl+NtUVF0sA7MsXsP1UyJoMp4YLEuNSfAP+JcXn/tWtIaxVXM" crossorigin="anonymous"></script>

<ng-container *ngIf="programForm">
  <div class="container">
    <form [formGroup]="programForm" (ngSubmit)="signUp()" class="form">
    <div class="form-group mt-5">
      <label for="firstName" class="form-label" >Prénom</label>
      <input id="firstName" type="text" formControlName="firstName" class="form-control">
      <div class="form-error" color="red"
            *ngIf="(programForm.controls['firstName'].touched) && 
              programForm.controls['firstName'].errors &&
              programForm.controls['firstName'].errors['required']"
        >
        Prénom obligatoire
      </div>
    </div>

    <div class="form-group mt-3">
      <label for="lastName">Nom</label>
      <input id="lastName" type="text" formControlName="lastName"  class="form-control">
    </div>

    <div class="form-group mt-3">
      <label for="email">E-mail</label>
      <input id="email" type="text" formControlName="email"  class="form-control">
    </div>

    <div class="form-group mt-3">
      <label for="phoneNumber">Numéro de téléphone</label>
      <input id="phoneNumber" type="text" formControlName="phoneNumber"   class="form-control">
    </div>

    <div class="form-group mt-3">
      <label for="role">Rôle</label>
      <input id="role" type="text" formControlName="role" class="form-control" >
    </div>

    <div class="form-group mt-3">
      <label for="password">Mot de passe</label>
      <input id="password" type="text" formControlName="password"  class="form-control">
    </div>

    <div class="form-group mt-3">
      <button type="submit" [disabled]="!programForm.valid" class="btn btn-primary mb-3">Enregistrer</button>
    </div>
    </form>
  </div>
</ng-container>
